garantykop[4] = "<br><img src=''http://i371.photobucket.com/albums/oo159/ThePowerstore/Advertentie/bliksem.png'' alt=''bliksem'' width=''20'' height=''15''>Garanzia 7 giorni soddisfatto o rimborsato";
